import React, { Component } from 'react'
import './app.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Navbar, Col, Row} from 'react-bootstrap'
import { BrowserRouter as Router, Route, Link, Switch, NavLink } from 'react-router-dom' 
import { Link as ScrollLink, DirectLink, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'
import Home from './Home'
import About from './About'
import Resume from './Resume'
import Portfolio from './Portfolio'
import Contact from './Contact'
import HomeBAW from './HomeBAW'
import AboutBAW from './AboutBAW'
import ResumeBAW from './ResumeBAW'
import PortfolioBAW from './PortfolioBAW'
import ContactBAW from './ContactBAW'
import FadeIn from 'react-fade-in';


class Body extends Component {
    constructor(props) {
        super(props)
        this.state = {
            isColorful: false
        }   
    }

    changeTheme = () => {
        this.setState({
            isColorful: !this.state.isColorful,
        })

    } 

    render() {

        const isColorful = this.state.isColorful

        const changeNavClass = () => {
            return (isColorful) ? 'colorful-font p-0' : 'BAW-font p-0'
        }

        const displayCurrentTheme = () => {
            return (isColorful) ? colorful : baw 
        } 

        const colorful =    
                        <Container fluid className="p-0 colorful-font">
                            <FadeIn transitionDuration={800}
                                    delay={100}> 
                                <Home />
                                <About />
                                <Portfolio />
                                <Resume />
                                <Contact />
                            </FadeIn>
                        </Container>

        const baw =     
                    <Container fluid className="p-0 BAW-font"> 
                        <HomeBAW />
                        <AboutBAW />
                        <PortfolioBAW />
                        <ResumeBAW />
                        <ContactBAW />
                    </Container>


        return (
            <Container fluid className="body p-0">
                <Router> 

                    <Row className={changeNavClass()}>
                      <Col>
                      test
                      </Col>
                      <Col>
                      test
                      </Col>
                      <Col>
                      test
                      </Col>
                      <Col>
                      test
                      </Col>
                      <Col>
                      test
                      </Col>
                      <Col>
                      test
                      </Col>
                    </Row>



                    {displayCurrentTheme()}




                    {/* <Container fluid className="p-0"> 
                        <Switch>
                            <Route exact path="/" Component="Home">
                                <Home />
                            </Route>
                            <Route exact path="/resume">
                                <Resume />
                            </Route>
                            <Route exact path="/Portfolio">
                                <Portfolio />
                            </Route>
                            <Route exact path="/Contact">
                                <Contact />
                            </Route>
                        </Switch>
                    </Container> */}

               </Router>
            </Container>
        )
    }
}

export default Body



