import React, {Component} from 'react'
import './app.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Row, Col } from 'react-bootstrap'
import FadeIn from 'react-fade-in';
import VisibilitySensor from 'react-visibility-sensor'

class AboutBAW extends Component {
    constructor(props) {
        super(props)

        this.state = {
             sectionRendered: false
        }
    }




    render() {

        const fadeList = () => {
            if(this.state.sectionRendered === false) {
                setTimeout(() => { 
                    this.setState({
                        sectionRendered:true 
                    }) 
                }, 4000)
                return  <FadeIn transitionDuration = {600} delay={600}>
                            <li>Writing</li>
                            <li>Finance</li>
                            <li>Photography</li>
                            <li>Programing (obviously)</li>
                            <li>Music Production / DJing</li>
                            <li>Calisthenics</li>
                        </FadeIn>
            } else {
                return <div> 
                            <li>Writing</li>
                            <li>Finance</li>
                            <li>Photography</li>
                            <li>Programing (obviously)</li>
                            <li>Music Production / DJing</li>
                            <li>Calisthenics</li>
                        </div>   
            }
        }




        const blank = <div> 
                        <li>Writing</li>
                        <li>Finance</li>
                        <li>Photography</li>
                        <li>Programing (obviously)</li>
                        <li>Music Production / DJing</li>
                        <li>Calisthenics</li>
                    </div> 




        return (
            <div className="aboutBAW" id="about">
                <Row className="row-height-set-100">
                    <Col className="d-flex justify-content-end align-items-center">
                        <span id="about-jared"> My name is Jared. I am a self-taught web developer with a wide range of interests, which include: </span>
                    </Col>
                    <Col className="d-flex justify-content-center align-items-center">
                        {/* <ul> */}
                            <VisibilitySensor  partialVisibility={true} offset={{top:10}}>
                            {({isVisible}) =>
                                <ul>
                                    {isVisible ?
                                    fadeList() : blank }
                                </ul>
                                }           
                            </VisibilitySensor>
                        {/* </ul> */}
                    </Col>
                </Row>
            </div>
        )
    }
}

export default AboutBAW














