import React, {Component} from 'react'
import { Particles } from "react-particles-js";
import './app.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Row, Col } from 'react-bootstrap'

class HomeBAW extends Component {
    constructor(props) {
        super(props)

        this.state = {
            screenWidth: window.innerWidth

        }
    }

    componentDidMount() {
        window.addEventListener('resize', () => {
            this.setState({
                screenWidth: window.innerWidth
            });
        }, false);
    }

    shouldComponentUpdate() {
        if(this.state.screenWidth <= 992) {
            return true
        } else {
            return false
        }
    }



    render() {

        const screenWidth = this.state.screenWidth

        const greeting = () => {
            const greetings = ["Hello", "Hey", "Greetings" ]
            const random = Math.floor(Math.random() * greetings.length)
            return greetings[random]
        }

        const lessParticles = () => {
            if(screenWidth < 700) {
                return 20
            } else {
                return 40
            }
        }

        return (
            <div className="homeBAW" id="home">
                <Particles className="resize-fix"
                params={{
                    "particles": {
                        "number": {
                            "value": lessParticles()
                        },
                        "size": {
                            "value": 3,
                            "random": true
                        },
                        "color": {
                            "value":"#000000"
                        },
                        "line_linked": {
                            "enable": true,
                            "opacity": 1,
                            "color": "#474747",
                            "width": 1
                        },
                        "move": {
                            "enable": true,
                            "straight": false,

                        }
                    },
                    "interactivity": {
                        "events": {
                            "onhover": {
                                "enable": true,
                                "mode": "grab"
                            },
                        },
                        "resize": true
                    }
                }} />

                <Row className="row-height-set-100">
                    <Col lg={6} xs={0}>

                    </Col>
                    <Col lg={6} xs={12} className="d-flex justify-content-center align-items-center" >
                        <span id="hello"> {greeting()} & welcome to my personal website. </span>
                    </Col>
                </Row>
            </div>
        )
    }
}

export default HomeBAW
