import React, { Component } from 'react'
import './app.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Row, Col } from 'react-bootstrap'
import ReactPlayer from 'react-player'
import VisibilitySensor from 'react-visibility-sensor'
import FadeIn from 'react-fade-in';


class Portfolio extends Component {
    constructor(props) {
        super(props)

        this.state = {
             sectionRendered: false
        }
    }

    render () {

        // const playVideo = () => {
        //     if(this.state.sectionRendered === false) {
        //         setTimeout(() => { 
        //             this.setState({
        //                 sectionRendered:true 
        //             }) 
        //         }, 4200)
        //         return  <ReactPlayer        className="d-flex justify-content-center align-end"
        //                                     url="videos/demo_video.MP4"
        //                                     muted={true}
        //                                     playing={true}
        //                                     loop={true}
        //                                     height='auto'
        //                                     width='100%' 
        //                                         />
        //     } else if (this.state.sectionRendered === true) {
        //         return <ReactPlayer     className="d-flex justify-content-center align-end"
        //                                 url="videos/demo_video.MP4"
        //                                 muted={true}
        //                                 playing={true}
        //                                 loop={true}
        //                                 height='auto'
        //                                 width='100%' 
        //                                     />  
        //     }
        // }

        const playVideo = () => {
            return      <FadeIn transitionDuration={800}> 
                            <ReactPlayer        className="d-flex justify-content-center align-end"
                                                        url="videos/demo_video.MP4"
                                                        muted={true}
                                                        playing={true}
                                                        loop={true}
                                                        height='auto'
                                                        width='100%' 
                                                        />
                        </FadeIn>
        }

        const prerender = <div style={{color: 'rgb(255, 128, 111)'}}> 
                                .
                            </div> 

        return (
            <div className="portfolio d-flex align-center" id="portfolio">
                <Row className="row-height-set-100">
                    <Col className="d-flex justify-content-end align-items-center" xs={5}>
                            <span id="portfolio-jared"> My recent projects include a financial dashboard, a music blog, and few personal websites. </span>
                    </Col>
                    <Col className="d-flex justify-content-center align-items-center" xs={7}>
                        <VisibilitySensor partialVisibility={true} offset={{top: 100}}>
                            {({isVisible}) => 
                                <div>
                                    {isVisible ?
                                    playVideo() : prerender }
                                </div>
                            }
                        </VisibilitySensor>
                    </Col>
                </Row>
            </div>
        )
    }
}

export default Portfolio

/// need to set above so when the screen is smaller than medium, it splits into two rows with the description on top and video player on bottom
