import React, { Component } from 'react'
import './app.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Row, Col } from 'react-bootstrap'
import ReactPlayer from 'react-player'


class Portfolio extends Component {
    constructor(props) {
        super(props)

        this.state = {
             sectionRendered: false
        }
    }

    render () {
        const fadeList = () => {
            if(this.state.sectionRendered === false) {
                setTimeout(() => { 
                    this.setState({
                        sectionRendered:true 
                    }) 
                }, 4200)
                return  <FadeIn transitionDuration = {600} delay={600}>
                            <li>Writing</li>
                            <li>Finance</li>
                            <li>Photography</li>
                            <li>Programing (obviously)</li>
                            <li>Music Production / DJing</li>
                            <li>Calisthenics</li>
                        </FadeIn>
            } else if (this.state.sectionRendered === true) {
                return <div> 
                            <li>Writing</li>
                            <li>Finance</li>
                            <li>Photography</li>
                            <li>Programing (obviously)</li>
                            <li>Music Production / DJing</li>
                            <li>Calisthenics</li>
                        </div>   
            }
        }

        const prerender = <div style={{color: 'white'}}> 
                                .
                            </div> 

        return (
            <div className="portfolio d-flex align-center" id="portfolio">
                <Row className="row-height-set-100">
                    <Col className="d-flex justify-content-end align-items-center" xs={5}>
                            <span id="portfolio-jared"> My recent projects include a financial dashboard, a music blog, and few personal websites. </span>
                    </Col>
                    <Col className="d-flex justify-content-center align-items-center" xs={7}>
                        <ReactPlayer    className="d-flex justify-content-center align-end"
                                                url="videos/demo_video.MP4"
                                                muted={true}
                                                playing={true}
                                                loop={true}
                                                height='auto'
                                                width='100%' 
                                                />
                    </Col>
                </Row>
            </div>
        )
    }
}

export default Portfolio

